#include <stdio.h>

int main() {
    int x = 10;
    float y = 5.5;
    char name[] = "Alice";

    printf("%d %.1f %s\n", x, y, name);
    return 0;
}

#include <stdio.h>
#include <stdbool.h>

int main() {
    int a = 5;
    float b = 3.14f;
    char c[] = "hello";
    bool d = true;

    printf("int: %d\nfloat: %.2f\nstring: %s\nbool: %d\n", a, b, c, d);
    return 0;
}

#include <stdio.h>

#define PI 3.14159  // Preprocessor constant

int main() {
    const int DAYS_IN_WEEK = 7;  // Constant variable
    printf("PI: %.5f\nDays in a week: %d\n", PI, DAYS_IN_WEEK);
    return 0;
}
